// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.3;
// import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "./RubyToken.sol";
import "./WODToken.sol";
import "./DogeHero.sol";
import "./GeneScience.sol";
contract DogeWar is AccessControlUpgradeable, PausableUpgradeable {//ERC721EnumerableUpgradeable
    // Percentage of difference of points to be in same range of lower and upper rivals.
    uint16 public lowerPointsThreshold;
    uint16 public upperPointsThreshold;
    uint112 public rubyPerBlock;
    uint112 public wodPerBlock;

    uint256 public lastRewardBlock;
    uint256 public totalShare;
    uint256 public accRubyPerShare;
    uint256 public accWODPerShare;
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;

    address public devAddress;
    address[] public allHeroOwners;
    RubyToken public rubyToken;
    DogeHero public dogeHero;
    GeneScience public geneScience;
    WODToken public wodToken;
    bytes32 public constant CEO_ROLE = keccak256("CEO_ROLE");
    mapping(address => uint256) public heroEntered;
    mapping(address => uint256) private heroOwnerAdded;
    mapping (address => Hero) public heroInfo;

    // Mapping from token ID to owner address
    mapping(uint256 => address) private _owners;

    // Mapping owner address to token count
    mapping(address => uint256) private _balances;

    // Mapping from owner to list of owned token IDs
    mapping(address => mapping(uint256 => uint256)) private _ownedTokens;

    // Mapping from token ID to index of the owner tokens list
    mapping(uint256 => uint256) private _ownedTokensIndex;
    // Mapping from address to position in the allHeroes array
    mapping(address => uint256) private _allHeroOwnersIndex;

    struct Hero{
        uint64 rank;
        uint64 share;
        uint64 health;
        uint64 maxHealth;
        uint64 attack;
        uint64 defense;
        uint64 healthRecovery;
        uint64 lastRecoverBlock;
        uint256 rubyDebt;
        uint256 wodDebt;
    }
    //mapping (uint256 => Hero) public tokenIdToHero;
    // event EnterWar(uint256 heroId);
    // event LeaveWar(uint256 heroId);
    event AttackDoge(address indexed attacker, address indexed defender, uint8 status, uint256 reward);
    modifier nonReentrant(address heroOwner) {        
        require(heroEntered[heroOwner] != _ENTERED);        
        heroEntered[heroOwner] = _ENTERED;
        _;        
        heroEntered[heroOwner] = _NOT_ENTERED;
    }
    
    function initialize() public initializer{
        __AccessControl_init();
        __Pausable_init();
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(CEO_ROLE, msg.sender);
        devAddress = msg.sender;
        rubyPerBlock = 1000*10**18;
        wodPerBlock = 10*10**18;
        lowerPointsThreshold = 35;//35%
        upperPointsThreshold = 1000;//1000%
    }
