    function setDev(address newDev) external onlyRole(DEFAULT_ADMIN_ROLE){
        require(newDev != address(0));
        devAddress = newDev;
    }
    function setRubyToken(address _rubyToken) external onlyRole(CEO_ROLE){
        rubyToken = RubyToken(_rubyToken);
    }
    function setGeneScience(address _geneScience) external onlyRole(CEO_ROLE){
        geneScience = GeneScience(_geneScience);
    }
    function setDogeHero(address _dogeHero) external onlyRole(CEO_ROLE){
        dogeHero = DogeHero(payable(_dogeHero));
    }
    function setWODToken(address _wodToken) external onlyRole(CEO_ROLE) {
        wodToken = WODToken(_wodToken);
    }
    function setRubyPerBlock(uint72 _rubyPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE){
        rubyPerBlock = _rubyPerBlock;
    }
    function setWODPerBlock(uint72 _wodPerBlock) external onlyRole(DEFAULT_ADMIN_ROLE) {
        wodPerBlock = _wodPerBlock;
    }
    function pause() external onlyRole(CEO_ROLE) whenNotPaused{
        _pause();
    }
    function unpause() external onlyRole(CEO_ROLE) whenPaused{
        _unpause();
    }
   
    function heroEnter(uint256[] memory heroIds) external whenNotPaused nonReentrant(msg.sender){
        updateReward();
        Hero storage hero = heroInfo[msg.sender];
        if(hero.share > 0){
            _harvestReward(hero, msg.sender);
        } else {
            if(heroOwnerAdded[msg.sender] != _ENTERED){
                _addHeroToAllHeroes(msg.sender);
            }
        }
        for(uint256 i = 0; i < heroIds.length; i++){
            _enterWar(msg.sender, heroIds[i]);
        }
        _addHeroes(hero, heroIds);
        _updateRewardDebt(hero);
    }
    
    function heroLeave(uint256 amount) external whenNotPaused nonReentrant(msg.sender){
        uint256 balance = balanceOf(msg.sender);
        require(balance >= amount);
        updateReward();
        Hero storage hero = heroInfo[msg.sender];
        _harvestReward(hero, msg.sender);
        uint256[] memory heroIds = new uint256[](amount);
        uint256 originalBalance = balance;
        for(uint256 i = 0; i < amount; i++){
            balance--;
            //leave the last hero to avoid swapping cost in _removeTokenFromOwnerEnumeration function
            uint256 heroId = tokenOfOwnerByIndex(msg.sender, balance);
            heroIds[i] = heroId;
            _exitWar(msg.sender, heroId);
        }
        _removeHeroes(hero, heroIds);
        _updateRewardDebt(hero);
        if(originalBalance == amount){
            _retireHero(msg.sender);
        }
    }

    function heroLeave2(uint256[] memory heroIds) external whenNotPaused nonReentrant(msg.sender){       
        require(_validOwner(msg.sender, heroIds));
        updateReward();
        Hero storage hero = heroInfo[msg.sender];
        _harvestReward(hero, msg.sender);
        //leave multi heroes
        for(uint256 i = 0; i < heroIds.length; i++){
            _exitWar(msg.sender, heroIds[i]);
        }
        _removeHeroes(hero, heroIds);
        _updateRewardDebt(hero);
        if(balanceOf(msg.sender) == 0){
            _retireHero(msg.sender);
        }
    }
    
    //this function will fail if there are too many heroes to leave
    function leaveAll() external whenNotPaused nonReentrant(msg.sender){
        uint256 balance = balanceOf(msg.sender);
        require(balance > 0);
        updateReward();
        Hero storage hero = heroInfo[msg.sender];
        _harvestReward(hero, msg.sender);
        totalShare -= hero.share;
        delete heroInfo[msg.sender];
        _retireHero(msg.sender);
        _transferHeroes(msg.sender, balance);
    }
    function attackDoge(address defenderAddress) external whenNotPaused nonReentrant(msg.sender){
        updateReward();
        // address defenderAddress = ownerOf(defenderId);
        require(balanceOf(defenderAddress) > 0);
        require(areInPointsRange(msg.sender, defenderAddress));
        
        Hero storage defender = heroInfo[defenderAddress];
        Hero storage attacker = heroInfo[msg.sender];
        _recoverHero(defender);
        _recoverHero(attacker);
        // require(!heroEntered[defenderAddress]);
        // heroEntered[defenderAddress] == true;
       
        uint64 trueDamageOfAttacker;
        uint64 trueDamageOfDefender;
        uint64 attackerDefense = attacker.defense;
        uint64 attackerAttack = attacker.attack;
        uint64 attackerHealth = attacker.health;

        uint64 defenderDefense = defender.defense;
        uint64 defenderAttack = defender.attack;
        uint64 defenderHealth = defender.health;

        if(defenderDefense < attackerAttack){
            trueDamageOfAttacker = attackerAttack - defenderDefense;            
            defenderHealth = (defenderHealth > trueDamageOfAttacker) ? (defenderHealth - trueDamageOfAttacker) : 0;
        }

        if(attackerDefense < defenderAttack){
            trueDamageOfDefender = defenderAttack - attackerDefense;            
            attackerHealth = (attackerHealth > trueDamageOfDefender) ? (attackerHealth - trueDamageOfDefender) : 0;
        }
        uint256 reward;
        uint8 status;//=0: draw
        if(defenderHealth == 0){
            //attacker will rob all defender's ruby
            status = 1;//=1: win
            reward = _harvestRuby(defender, msg.sender);            
            _updateRubyDebt(defender);
            if(defender.rank > 0) defender.rank--;
            attacker.rank++;
        }
        if(attackerHealth == 0){
            //attacker will lose all his ruby
            status = 2;//=2: lose
            reward = _harvestRuby(attacker, defenderAddress);
            _updateRubyDebt(attacker);
            if(attacker.rank > 0) attacker.rank--;
            defender.rank++;
        }
        if(trueDamageOfDefender != 0){
            attacker.health = attackerHealth;
        }
        if(trueDamageOfAttacker != 0){
            defender.health = defenderHealth;
        }
        //end attack
        // heroEntered[defenderAddress] == false;
        emit AttackDoge(msg.sender, defenderAddress, status, reward);
    }
    function harvestReward() external whenNotPaused nonReentrant(msg.sender){        
        updateReward();
        Hero storage hero = heroInfo[msg.sender];
        if(hero.share > 0){
            _harvestReward(hero, msg.sender);
        }        
        _updateRewardDebt(hero);
    }
    function pendingReward() external view returns(uint256 pendingRuby, uint256 pendingWOD){
        Hero memory hero = heroInfo[msg.sender];
        uint256 rubyPerShare = accRubyPerShare;
        uint256 wodPerShare = accWODPerShare;
        if (block.number > lastRewardBlock && totalShare != 0) {
            uint256 rubyReward = _getRubyReward(lastRewardBlock);
            uint256 wodReward = _getWODReward(lastRewardBlock);
            rubyPerShare += (rubyReward * 1e12) / totalShare;
            wodPerShare += (wodReward * 1e12) / totalShare;
        }         
        pendingRuby = ((hero.share * rubyPerShare) / 1e12) - hero.rubyDebt;
        pendingWOD = ((hero.share * wodPerShare) / 1e12) - hero.wodDebt;
    }
    
    function updateReward() public {
        uint256 lastBlock = lastRewardBlock;
        uint256 share = totalShare;
        if (block.number <= lastBlock) {
            return;
        }
        if (share == 0) {            
            lastRewardBlock = block.number;
            return;
        }
        
        uint256 rubyReward = _getRubyReward(lastBlock);
        uint256 wodReward = _getWODReward(lastBlock);
        _mintRuby(address(this), rubyReward);
        _mintWOD(address(this), wodReward);
        accRubyPerShare += (rubyReward * 1e12) / share;
        accWODPerShare += (wodReward * 1e12) / share;
        
        lastRewardBlock = block.number;
    }
    
    function areInPointsRange(address _attacker, address _defender) public view returns (bool) {
        Hero memory attacker = heroInfo[_attacker];
        Hero memory defender = heroInfo[_defender];
        uint256 attackerRank = attacker.rank;
        uint256 defenderRank = defender.rank;

        if (_attacker == address(0) || _defender == address(0) || _attacker == _defender) return false;
        if (attackerRank < 20) return true;

        uint256 pointsPercentageDifference;
        if (attackerRank < defenderRank) {
        // Will only be in range if the difference between points is less % than upperPointsThreshold
        pointsPercentageDifference = ((defenderRank * 100) / attackerRank) - 100;
        return pointsPercentageDifference < upperPointsThreshold;
        } else {
        uint256 rankDifference = attackerRank - defenderRank;
        // Will only be in range if the difference between points is less % than lowerPointsThreshold
        pointsPercentageDifference = rankDifference * 100 / attackerRank;
        return pointsPercentageDifference < lowerPointsThreshold;
        }
    }
    /**
    * @notice Set Points Threshold.
    * @dev Function to set the points threshold.
    * @param _lowerPointsThreshold its the max percentage of difference for a lower rival.
    * @param _upperPointsThreshold its the max percentage of difference for an upper rival.
    */
    function setPointsThreshold(uint16 _lowerPointsThreshold, uint16 _upperPointsThreshold) external onlyRole(CEO_ROLE) {
        require(_lowerPointsThreshold > 0 && _lowerPointsThreshold < 100);
        require(_upperPointsThreshold > 0);
        lowerPointsThreshold = _lowerPointsThreshold;
        upperPointsThreshold = _upperPointsThreshold;
    }

    function balanceOf(address owner) public view returns (uint256) {
        require(owner != address(0), "Balance query for the zero address");
        return _balances[owner];
    }

    function ownerOf(uint256 tokenId) public view returns (address) {
        address owner = _owners[tokenId];
        require(owner != address(0), "Owner query for nonexistent token");
        return owner;
    }
    function tokenOfOwnerByIndex(address owner, uint256 index) public view returns (uint256) {
        require(index < balanceOf(owner), "Owner index out of bounds");
        return _ownedTokens[owner][index];
    }

    function getTotalHeroOwner() external view returns(uint256){
        return allHeroOwners.length;
    }
    function _mint(address to, uint256 tokenId) internal {
        require(to != address(0), "Mint to the zero address");
        _beforeTokenTransfer(address(0), to, tokenId);
        _balances[to] += 1;
        _owners[tokenId] = to;
    }

    function _burn(uint256 tokenId) internal {
        address owner = ownerOf(tokenId);
        _beforeTokenTransfer(owner, address(0), tokenId);
        _balances[owner] -= 1;
        delete _owners[tokenId];
    }
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal {
        if (from == address(0)) {
            // _addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0)) {
            // _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }
    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }
    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId) private {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }
    
    function _enterWar(address owner, uint256 heroId) internal{
        require(!dogeHero.isPregnant(heroId));
        dogeHero.transferFrom(owner, address(this), heroId);
        _mint(owner, heroId);
    }
    function _safeRubyTransfer(address _to, uint256 _amount) internal {
        uint256 rubyBal = rubyToken.balanceOf(address(this));
        if (_amount > rubyBal) {
            rubyToken.transfer(_to, rubyBal);
        } else {
            rubyToken.transfer(_to, _amount);
        }
    }
    function _safeWODTransfer(address _to, uint256 _amount) internal {
        uint256 wodBal = wodToken.balanceOf(address(this));
        if (_amount > wodBal) {
            wodToken.transfer(_to, wodBal);
        } else {
            wodToken.transfer(_to, _amount);
        }
    }
    function _harvestReward(Hero storage hero, address receiver) internal {
        _harvestRuby(hero, receiver);
        _harvestWOD(hero, receiver);
    }
    
    function _harvestRuby(Hero storage hero, address receiver) internal returns(uint256) {        
        uint256 pending = ((hero.share * accRubyPerShare) / 1e12) - hero.rubyDebt;
        if(pending > 0) {
            _safeRubyTransfer(receiver, pending);
        }        
        return pending;
    }
    function _harvestWOD(Hero storage hero, address receiver) internal returns(uint256) {        
        uint256 pending = ((hero.share * accWODPerShare) / 1e12) - hero.wodDebt;
        if(pending > 0) {
            _safeWODTransfer(receiver, pending);
        }        
        return pending;
    }
    function _getHeroTraits(uint256 heroId) internal view returns(uint8[12] memory traits){
        (,,,,,,,,,uint256 genes) = dogeHero.getDoge(heroId);
        traits = geneScience.expressingTraits(genes);
    }
    function _recoverHero(Hero storage hero) internal{
        uint256 multiplier = block.number - hero.lastRecoverBlock;
        if(multiplier > 0){
            uint256 recoverHealth = hero.healthRecovery * multiplier;
            if(recoverHealth >= (hero.maxHealth - hero.health)){
                hero.health = hero.maxHealth;
            } else{
                hero.health += uint64(recoverHealth);
            }
            hero.lastRecoverBlock = uint64(block.number);
        }        
    }
    function _updateRewardDebt(Hero storage hero) internal {        
        _updateRubyDebt(hero);
        _updateWODDebt(hero);
    }
    function _updateRubyDebt(Hero storage hero) internal {
        hero.rubyDebt = (hero.share * accRubyPerShare) / 1e12;
    }
    function _updateWODDebt(Hero storage hero) internal {
        hero.wodDebt = (hero.share * accWODPerShare) / 1e12;
    }
    function _getHeroStat(uint256 heroId) internal view returns(uint64 share, 
        uint64 health, 
        uint64 attack, 
        uint64 defense,
        uint64 recoverHealth){
        uint8[12] memory traits = _getHeroTraits(heroId);
        share = _getTraitValue(traits[0]) + _getTraitValue(traits[1]) + _getTraitValue(traits[2]);
        health = _getTraitValue(traits[3]) + _getTraitValue(traits[4]) + _getTraitValue(traits[5]);
        attack = _getTraitValue(traits[6]) + _getTraitValue(traits[7]) + _getTraitValue(traits[8]);
        defense = _getTraitValue(traits[9]) + _getTraitValue(traits[10]); 
        recoverHealth = _getTraitValue(traits[11]);
    }    
    
    function _getTraitValue(uint8 trait) internal pure returns(uint16 traitValue){
        if(trait < 16) traitValue = trait;
        else if(trait < 24) traitValue = 2 * trait;
        else if(trait < 28) traitValue = 6 * trait;
        else if(trait < 30) traitValue = 24 * trait;
        else traitValue = 120 * trait;
    }
    function _mintRuby(address account, uint256 amount) internal{
        rubyToken.mint(account, amount);
        rubyToken.mint(devAddress, amount/10);
    }
    function _mintWOD(address account, uint256 amount) internal {        
        if(amount > 0){
            wodToken.mint(account, amount);
            wodToken.mint(devAddress, amount/10);
        }
    }
    function _getRubyReward(uint256 lastBlock) private view returns(uint256) {
        return (block.number - lastBlock) * rubyPerBlock;
    }
    function _getWODReward(uint256 lastBlock) private view returns(uint256){
        uint256 wodReward = (block.number - lastBlock) * wodPerBlock;

        uint256 totalWOD = wodToken.totalSupply();
        uint256 maxWOD = wodToken.MAX_SUPPLY();
        if(totalWOD + (wodReward*11)/10 > maxWOD){
            wodReward = ((maxWOD - totalWOD)*10)/11;
        }
        return wodReward;
    }
    function _addHeroes(Hero storage hero, uint256[] memory heroIds) internal {
        uint64 share;
        uint64 health;
        uint64 attack;
        uint64 defense;
        uint64 recoverHealth;
        for(uint256 i = 0; i < heroIds.length; i++){
            (uint64 _share, 
            uint64 _health, 
            uint64 _attack, 
            uint64 _defense, 
            uint64 _recoverHealth) = _getHeroStat(heroIds[i]); 
            share += _share;
            health += _health;
            attack += _attack;
            defense += _defense;
            recoverHealth += _recoverHealth;
        }
        totalShare += share;
        hero.share += share;
        hero.maxHealth += health;
        hero.health = hero.maxHealth;
        hero.healthRecovery += recoverHealth;
        hero.attack += attack;
        hero.defense += defense;
        hero.lastRecoverBlock = uint64(block.number);
    }
    
    function _removeHeroes(Hero storage hero, uint256[] memory heroIds) internal {
        uint64 share;
        uint64 health;
        uint64 attack;
        uint64 defense;
        uint64 recoverHealth;
        for(uint256 i = 0; i < heroIds.length; i++){
            (uint64 _share, 
            uint64 _health, 
            uint64 _attack, 
            uint64 _defense, 
            uint64 _recoverHealth) = _getHeroStat(heroIds[i]); 
            share += _share;
            health += _health;
            attack += _attack;
            defense += _defense;
            recoverHealth += _recoverHealth;
        }
        totalShare -= share;
        hero.share -= share;
        hero.maxHealth -= health;
        hero.health = hero.maxHealth;
        hero.healthRecovery -= recoverHealth;
        hero.attack -= attack;
        hero.defense -= defense;
        hero.lastRecoverBlock = uint64(block.number);
    }
    function _exitWar(address receiver, uint256 heroId) internal{
        // The exit is good! Remove the escrow heroID before sending the heroID
        // to the sender so we can't have a reentrancy attack.
        _burn(heroId);
        dogeHero.transferFrom(address(this), receiver, heroId);        
    }
    function _transferHeroes(address receiver, uint256 totalHeroes) internal{
        uint256 index = totalHeroes;
        for(uint256 i = 0; i < totalHeroes; i++){
            index--;
            _exitWar(receiver, tokenOfOwnerByIndex(receiver, index));
        }
    }
    function _validOwner(address owner, uint256[] memory heroIds) internal view returns(bool valid){
        valid = true;
        for(uint256 i = 0; i < heroIds.length; i++){            
            if(ownerOf(heroIds[i]) != owner){
                return false;
            }
        }
    }
    function _retireHero(address _heroOwner) private {        
        uint256 lastHeroIndex = allHeroOwners.length - 1;
        uint256 currentHeroIndex = _allHeroOwnersIndex[_heroOwner];

        if(lastHeroIndex != currentHeroIndex){
            address lastHeroOwner = allHeroOwners[lastHeroIndex];

            allHeroOwners[currentHeroIndex] = lastHeroOwner; // Move the last token to the slot of the to-delete token
            _allHeroOwnersIndex[lastHeroOwner] = currentHeroIndex; // Update the moved token's index
        }       
        // This also deletes the contents at the last position of the array
        delete _allHeroOwnersIndex[_heroOwner];
        allHeroOwners.pop();
        heroOwnerAdded[msg.sender] = _NOT_ENTERED;
    }
    function _addHeroToAllHeroes(address _heroOwner) private {
        heroOwnerAdded[msg.sender] = _ENTERED;
        _allHeroOwnersIndex[_heroOwner] = allHeroOwners.length;
        allHeroOwners.push(_heroOwner);
    }
